<?phpnamespace Sonover\Couchbase\Eloquent;use Ramsey\Uuid\Uuid as UID;trait UUID{    /**     * Boot function from laravel.     */    protected static function boot()    {        parent::boot();        static::creating(function ($model) {            $model->incrementing = false;            $model->{$model->getKeyName()} = (string) UID::uuid4();        });    }}