<?phpnamespace Sonover\Couchbase\Tests\Eloquent;use Sonover\Couchbase\Eloquent\Model;use Sonover\Couchbase\Tests\Testcase;class ModelTest extends TestCase{    /** @test * */    function it_saves_a_new_user()    {        $user = new UserStub();        $user->first_name = 'Clevon';        $user->last_name = "Noel";        $saved = $user->save();        $this->assertTrue($saved);        $this->assertEquals('Clevon', $user->first_name);        $this->assertEquals('Noel', $user->last_name);    }}class UserStub extends Model {}