<?phpnamespace Sonover\Couchbase\Tests;use Sonover\Couchbase\CouchbaseServiceProvider;abstract class Testcase extends \Orchestra\Testbench\TestCase{    /**     * Define environment setup.     *     * @param  \Illuminate\Foundation\Application  $app     * @return void     */    protected function getEnvironmentSetUp($app)    {        $app['config']->set('database.default', 'couchbase');        $app['config']->set('database.connections.couchbase', [            'driver' => 'couchbase',            'host' => 'couchbase://localhost/',            'database' => 'pure_app_testing',            'bucket' => 'pure_app_testing'        ]);    }    /**     * Get package providers.     *     * @param  \Illuminate\Foundation\Application  $app     * @return array     */    protected function getPackageProviders($app)    {        return [           CouchbaseServiceProvider::class,        ];    }}